import { useRef, useState } from "react";
import { Link } from "react-router-dom";
import Style from './dropDown.module.css';



const DropDown = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
  setIsMenuOpen(!isMenuOpen);

  };

    return (
      <div className="member-info" style={{position:"relative"}}>
      <div className="member-info__avatar" style={{width:"40px", paddingRight:"70px", paddingBottom:"30px"}} onClick={toggleMenu}>
        <img className="userImg" src="/images/userSL.png" alt="User" />
      </div>
      {isMenuOpen && (
        <div className={Style.menu}>
          <ul className={Style.dropMenu} style={{position:"relative"}}>
            <li style={{listStyle:"none", top:"0", left:"50%", fontSize:"x-large"}}>
              **님 반갑습니다!
            </li>
            <li style={{listStyle:"none", top:"0", left:"50%", paddingRight:"30px"}}>
              <Link to="/mypage/myteam"> MY </Link>
            </li>
            <li style={{listStyle:"none", top:"0", left:"50%", transform:"translateX(-50%)"}}>
              <Link to ="/notice">고객센터</Link>
            </li>
            <li style={{listStyle:"none", top:"0", left:"50%", transform:"translateX(-50%)"}}>
              <button onSubmit={{}}>로그아웃</button>
            </li>
          </ul>
        </div>
      )}
    </div>
      );
    };


export default DropDown;
