import axios from 'axios';
import { replyReducers } from '../reducers/replyReducer';
import { baseUrl } from '../../apiurl';

function getReplyList(talkNum) {
  return async (dispatch) => {
    const data = await axios
      .get(`${baseUrl}/footballreply/list/${talkNum}`)
      .then((response) => response.data);
    console.log(data);
    dispatch(replyReducers.getReplyList({ data }));
  };
}

//formData => 넘겨줄 데이터 / config => 옵션
function replyCreate(formData, config) {
  return async (dispatch) => {
    await axios
      .post(`${baseUrl}/footballreply/create`, formData, config)
      .then((response) => response.data);
  };
}

function replyUpdate(formData, config) {
  return async (dispatch) => {
    await axios
      .put(`${baseUrl}/footballreply/update`, formData, config)
      .then((response) => response.data);
  };
}

function replyDelete(talkReplyNum, config) {
  return async (dispatch) => {
    await axios
      .delete(`${baseUrl}/footballreply/delete/${talkReplyNum}`, config)
      .then((response) => response.data);
  };
}

export const replyActions = {
  getReplyList,
  replyCreate,
  replyUpdate,
  replyDelete,
};
