// board_reducer에서 받아온 데이터를 사용하기

import { useDispatch, useSelector } from "react-redux";
import { boardActions } from "../../redux/actions/board_action";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const BoardList = () => {
  const dispatch = useDispatch();

  const navigator = useNavigate();

  const boardList = useSelector((state) => state.board.boardList); // board에 있는 boardList를 가지고 오겠다.

  const getBoardList = (currentPage) => {
    dispatch(boardActions.getBoardList(currentPage));
    navigator(`/board/list/${currentPage}`);
  };

  useEffect(() => {
    getBoardList(1); // 1번을 넘겨줌
  }, []); // 한번만 요청이 되도록 설정

  return (
    <div>
      <h3 className="text-center">게시판 목록</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <colgroup>
          <col width="8%" />
          <col width="*" />
          <col width="12%" />
          <col width="12%" />
        </colgroup>

        <thead>
          <th>번호</th>
          <th>제목</th>
          <th>작성자</th>
          <th>조회수</th>
        </thead>

        <tbody>
          {boardList &&
            boardList.map((board) => {
              {
                <tr key={board.num}>
                  <th>${board.num}</th>
                </tr>;
              }
            })}
        </tbody>
        <p>{console.log(BoardList.length)}</p>
      </table>
    </div>
  );
};

export default BoardList;
