import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { boardActions } from "../../reduxs/actions/board_action";

const BoardUpdate = () => {
  const navigator = useNavigate();
  const dispatch = useDispatch();
  const { num } = useParams();

  const [inputs, setInputs] = useState({
    subject: "",
    content: "",
    filename: null,
  });

  const { subject, content, filename } = inputs;

  const board = useSelector((state) => state.board.boardDetail);
  const pv = useSelector((state) => state.board.pv);

  useEffect(() => {
    setInputs(board);
  }, []);

  const handleValueChange = (e) => {
    e.preventDefault();
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    setInputs((prev) => {
      return { ...prev, ...nextState };
    });
  };

  const handleFileChange = (e) => {
    e.preventDefault();
    setInputs({ ...inputs, [e.target.name]: e.target.files[0] }); // 값만 바꿀거면 이렇게 해주면됨
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("num", num);
    formData.append("subject", subject);
    formData.append("content", content);
    formData.append("currentPage", pv.currentPage);

    console.log("filename", filename);
    if (filename != null) formData.append("filename", filename);

    const config = {
      headers: { "Content-Type": "multipart/form-data" },
    };

    await dispatch(boardActions.getBoardUpdate(formData, config));

    // 초기화 시켜주기 위해서
    setInputs({
      subject: "",
      content: "",
      filename: null,
    });

    // 글을 다 쓰고나서 리스트 가져오기
    // 답변글이면 그 페이지로 가고, 제목글이면 첫페이지로 가도록함
    navigator(`/board/list/${pv.currentPage}`);
  };

  const handleReset = (e) => {
    e.preventDefault();
    setInputs(board);
  };

  const handleBack = (e) => {
    e.preventDefault();
    navigator(-1);
  };

  return (
    <div>
      <form name="frm" encType="multipart/form-data">
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <tbody>
            <tr>
              <th width="20%">글쓴이</th>
              <td>{board.writer}</td>
              <th width="20%">등록일</th>
              <td>{board.reg_date}</td>
            </tr>

            <tr>
              <th>제목</th>
              <td colSpan="3">
                <input
                  type="text"
                  name="subject"
                  id="subject"
                  defaultValue={board.subject}
                  value={subject}
                  onChange={handleValueChange}
                />
              </td>
            </tr>

            <tr>
              <th>내용</th>
              <td colSpan="3">
                <textarea
                  name="content"
                  id="content"
                  rows="13"
                  cols="40"
                  defaultValue={board.content}
                  value={content}
                  onChange={handleValueChange}
                ></textarea>
              </td>
            </tr>

            <tr>
              <th>첨부파일</th>
              <td colSpan="3">
                <input
                  type="file"
                  name="filename"
                  id="filepath"
                  onChange={handleFileChange}
                />
                <span>
                  {board.upload
                    ? board.upload.substring(board.upload.indexOf("_") + 1)
                    : null}
                </span>
              </td>
            </tr>
          </tbody>
        </table>

        <button className="btn btn-primary" onClick={handleUpdate}>
          수정
        </button>
        <button className="btn btn-primary" onClick={handleReset}>
          취소
        </button>
        <button className="btn btn-primary" onClick={handleBack}>
          뒤로
        </button>
      </form>
    </div>
  );
};

export default BoardUpdate;
