import { Link } from "react-router-dom";

const TableRow = (props) => {
  const { memberz } = props;

  // 회원 탈퇴 함수 선언
  const updateMemberLeave = async () => {
    <>
      if (memberz.memberLeave === "T") alert("이미 탈퇴된 회원입니다."); return;
    </>;

    alert("회원 탈퇴 완료");
    console.log(memberz.memberNum);
  };

  return (
    <tr>
      <td>{memberz.memberNum}</td>
      <td>
        {/* <Link to={`/admin/member/view/${notice.noticeNum}`}> */}
        {memberz.memberEmail}
        {/* </Link> */}
      </td>
      <td>{memberz.memberProfile}</td>
      <td>{memberz.memberReg}</td>
      <td>{memberz.memberLeave}</td>
      <td>
        <Link className="btn btn-outline-danger" onClick={updateMemberLeave}>
          탈퇴
        </Link>
      </td>
    </tr>
  );
};

export default TableRow;
