import { Link } from "react-router-dom";
import { baseURL } from "../../apiurl";
import axios from "axios";
const TableRow = (props) => {
  const { video } = props;

  const linkToComments = async () => {
    await axios.post(`${baseURL}/admin/video/list/${video.videoNum}`);

    window.location.reload(); // 페이지 새로고침
  };

  return (
    <tr>
      <td>{video.videoNum}</td>
      <td>
        <Link to={`${video.videoUrl}`}>
          <img src={`${video.thumbnailUrl}`} />
        </Link>
      </td>
      {/* <td>{video.thumbnailUrl}</td> */}

      <td>
        <Link to={`/admin/video/view/${video.videoNum}`}>
          {video.videoName}
        </Link>
      </td>

      <td>{video.videoType}</td>
      <td>
        <button className="btn btn-outline-danger" onClick={linkToComments}>
          이동
        </button>
      </td>
    </tr>
  );
};

export default TableRow;
