// 어디에 접근해서 어떤 데이터를 가지고 와야 하는지 정의

import axios from "axios";
import { baseURL } from "../../apiurl";
import { noticeReducers } from "../reducers/notice_reducer";
//
function getNoticeList(currentPage) {
  // 함수명 없이 바로 사용한다.
  // board_reducer에 있는 친구를 사용한다.
  return async (dispatch) => {
    ////경로를 써준다...백엔드를 요청하고 결과값을 받을때까지 기다린다.(await)
    const data = await axios
      .get(`${baseURL}/admin/notice/list/${currentPage}`)
      .then((response) => response.data); // 넘겨주는 값을 받을 때는 무조건 response.data를 사용한다.

    console.log(data);
    dispatch(noticeReducers.getNoticeList({ data }));
  };
}

function getNoticeDetail(num, config) {
  return async (dispatch) => {
    //data 값을 reduce를 이용해서 store에 저장해야함
    const data = await axios
      .get(`${baseURL}/admin/board/view/${num}`, config)
      .then((response) => response.data); // backend에서 /board/view/ 이렇게 요청하도록 되어있어서 이렇게 주소를 써줌 -> 결과값을 받아옴 .then()
    dispatch(noticeReducers.getNoticeDetail({ data }));
  };
}

function getNoticeDelete(num, config) {
  return async (dispatch) => {
    await axios
      .delete(`${baseURL}/admin/notice/delete/${num}`, config)
      .then((response) => response.data);
  };
}

function getNoticeWrite(formData, config) {
  return async () => {
    await axios
      .post(`${baseURL}/admin/notice/write`, formData, config)
      .then((response) => response.data);
  };
}

function getNoticeUpdate(formData, config) {
  return async () => {
    await axios
      .put(`${baseURL}/admin/notice/update`, formData, config)
      .then((response) => response.data);
  };
}

// 어디에 있는거를 호출하는지 이름으로 구분 가능하다. 어느 라이브러리에 있는 어느 함수인지
export const noticeActions = {
  getNoticeList,
  getNoticeDetail,
  getNoticeDelete,
  getNoticeWrite,
  getNoticeUpdate,
}; // 여기에 등록을 해주어야 외부에서 접근할 수 있음

// 바로 호출할거면
