import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import { baseURL } from "../../apiurl";

const LoginPage = () => {
  const [inputs, setInputs] = useState({
    memberEmail: "",
    memberPass: "",
  });

  const { memberEmail, memberPass } = inputs;

  const handleValueChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const config = { headers: { "Content-Type": "application/json" } };

  const onSubmit = async (e) => {
    // 입력한 데이터를 보내는 작업
    e.preventDefault();
    await axios
      .post(`${baseURL}/login`, inputs, config) // 비번 유출 방지
      .then((response) => {
        console.log("responese :", response.data);

        let jwtToken = response.headers.get("Authorization");
        console.log(jwtToken);

        let jwtMemberName = response.data.memberName;
        let jwtMemberEmail = response.data.memberEmail;
        let jwtMemberRole = response.data.authRole;

        localStorage.setItem("Authorization", jwtToken);
      });
  };

  return (
    <div className="container text-center mt-5">
      <div className="mx-5">
        <h1>로그인</h1>
        <form onSubmit={onSubmit}>
          <div className="form-group mt-1">
            <input
              type="email"
              name="memberEmail"
              className="form-control"
              id="memberEmail"
              value={memberEmail}
              placeholder="이메일"
              maxLength="20"
              onChange={handleValueChange}
            />
          </div>
          <div className="form-group mt-1">
            <input
              type="password"
              className="form-control"
              name="memberPass"
              id="memberPass"
              value={memberPass}
              placeholder="비밀번호"
              maxLength="20"
              onChange={handleValueChange}
            />
          </div>
          <div className="mt-1">
            <button type="submit" className="btn btn-primary">
              로그인
            </button>
            <Link className="btn btn-primary" to="/joinadd">
              회원 가입
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
