import { useDispatch, useSelector } from "react-redux";
import { videoActions } from "../../reduxs/actions/video_action";
import { useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import TableRow from "./table_row";
import PageNavigation from "./page_nav";

const VideoList = () => {
  const dispatch = useDispatch();
  const navigator = useNavigate();

  const { currentPage } = useParams();
  const videoList = useSelector((state) => state.video.videoList);

  const pv = useSelector((state) =>
    state.video.pv ? state.video.pv : { currentPage: 1 }
  );

  const getVideoList = (currentPage) => {
    dispatch(videoActions.getVideoist(currentPage));
    navigator(`/admin/video/list/${currentPage}`);
  };

  useEffect(() => {
    getVideoList(currentPage);
  }, []); // 한번만 요청이 되도록 설정

  return (
    <div>
      <h3 className="text-center">회원 목록</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <colgroup>
          <col width="8%" />
          <col width="*%" />
          <col width="12%" />
          <col width="12%" />
          <col width="12%" />
          <col width="12%" />
        </colgroup>

        <thead>
          <tr>
            <th>회원 번호</th>
            <th>회원 이메일</th>
            <th>회원 닉네임</th>
            <th>가입일</th>
            <th>탈퇴여부</th>
            <th></th>
          </tr>
        </thead>

        <tbody>
          {videoList &&
            videoList.map((video) => {
              return <TableRow video={video} key={video.num} />;
            })}
        </tbody>
      </table>

      {pv ? <PageNavigation getVideoList={getVideoList} /> : ""}
    </div>
  );
};

export default VideoList;
