import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { videoActions } from "../../reduxs/actions/video_action";

const VideoUpdate = () => {
  const navigator = useNavigate();
  const dispatch = useDispatch();
  const { videoNum } = useParams();

  const [inputs, setInputs] = useState({
    // 영상 제목
    // 썸네일 주소
    // 비디오 주소
    // 동영상 타입 -> 드롭 다운으로 선택 되도록
    // 팀1
    //팀2
    //시즌
    videoName: "",
    thumbnailUrl: "",
    videoUrl: "",
    videoType: "",
    teamNum1: "",
    teamNum1: "",
    seasonNum1: "",
  });

  const {
    videoName,
    thumbnailUrl,
    videoUrl,
    videoType,
    teamNum1,
    teamNum2,
    seasonNum1,
  } = inputs;

  const video = useSelector((state) => state.video.videoDetail);
  const pv = useSelector((state) => state.video.pv);

  useEffect(() => {
    setInputs(video);
  }, []);

  const handleValueChange = (e) => {
    e.preventDefault();
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    setInputs((prev) => {
      return { ...prev, ...nextState };
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("videoNum", videoNum);
    formData.append("videoName", videoName);
    formData.append("currentPage", pv.currentPage);

    // 인증토큰을 가져와서 확인함.
    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: localStorage.getItem("Authorization"),
      },
    };

    await dispatch(videoActions.getVideoUpdate(formData, config));

    // 초기화 시켜주기 위해서
    setInputs({
      videoName: "",
      thumbnailUrl: "",
      videoUrl: "",
      videoType: "",
      teamNum1: "",
      teamNum1: "",
      seasonNum1: "",
    });

    // 글 수정 후 리스트로 이동
    navigator(`/admin/video/list/${pv.currentPage}`);
  };

  const handleReset = (e) => {
    e.preventDefault();
    setInputs(video);
  };

  const handleBack = (e) => {
    e.preventDefault();
    navigator(-1);
  };

  return (
    <div>
      <form name="frm" encType="multipart/form-data">
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <tbody>
            <tr>
              <th width="20%">영상 제목</th>
              <td colSpan="3">
                <textarea
                  name="videoName"
                  id="videoName"
                  rows="10"
                  cols="10"
                  defaultValue={video.videoName}
                  value={videoName}
                  onChange={handleValueChange}
                ></textarea>
              </td>
            </tr>

            <tr>
              <th>내용</th>
              <td colSpan="3">
                <textarea
                  name="videoUrl"
                  id="videoUrl"
                  rows="13"
                  cols="40"
                  defaultValue={video.videoUrl}
                  value={videoUrl}
                  onChange={handleValueChange}
                ></textarea>
              </td>
            </tr>
          </tbody>
        </table>

        <button className="btn btn-primary" onClick={handleUpdate}>
          수정
        </button>
        <button className="btn btn-primary" onClick={handleReset}>
          취소
        </button>
        <button className="btn btn-primary" onClick={handleBack}>
          뒤로
        </button>
      </form>
    </div>
  );
};

export default VideoUpdate;
