import axios from "axios";
import { useEffect, useState } from "react";
import { baseURL } from "../../apiurl";

const EditInfo = () => {
  const [members, setMembers] = useState({
    memberEmail: "",
    memberPass: "",
    memberName: "",
    memberPhone: "",
  });

  const config = {
    headers: {
      Authorization: localStorage.getItem("Authorization"),
    },
  };

  // 로컬 스토리지에 있는 정보를 받아와서 넘겨준다.
  const info = async () => {
    return await axios.get(
      `${baseURL}/member/editinfo/${localStorage.memberEmail}`
    );
    // return data;
  };
  useEffect(() => {
    console.log(info());
    // 함수 호출해서 info에 넣어준다.
    setMembers(...members, info(), { memberPass: "" });
  }, []);

  const handleValueChange = () => {};

  const onSubmit = () => {};

  return (
    <div className="container">
      <form onSubmit={onSubmit}>
        <div className="container">
          <h1>회원원정보 수정</h1>
          <div className="form-group mb-1">
            <input
              type="email"
              className="form-control"
              name="memberEmail"
              placeholder="이메일"
              value={localStorage.memberEmail}
              readOnly
            />
          </div>
          <div className="form-group mb-1">
            <input
              type="password"
              className="form-control"
              name="memberPass"
              placeholder="비밀번호"
              onChange={handleValueChange}
            />
          </div>
          <div className="form-group mb-1">
            <input
              type="text"
              className="form-control"
              name="memberName"
              placeholder="이름"
              onChange={handleValueChange}
            />
          </div>

          <div className="form-group mb-1">
            <input
              type="text"
              className="form-control"
              name="memberPhone"
              placeholder="연락처"
              onChange={handleValueChange}
            />
          </div>

          <button type="submit" className="btn btn-primary">
            회워 정보 수정
          </button>
        </div>
      </form>
    </div>
  );
};
export default EditInfo;
