import { useDispatch, useSelector } from "react-redux";
import { noticeActions } from "../../reduxs/actions/notice_action";
import { useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import TableRow from "./table_row";
import PageNavigation from "./page_nav";

const NoticeList = () => {
  const dispatch = useDispatch();
  const navigator = useNavigate();

  const { currentPage } = useParams(); // 받아 온 값을 아래에서 사용한다.
  const noticeList = useSelector((state) => state.notice.noticeList); // board에 있는 boardList를 가지고 오겠다.

  // 페이징 처리
  // state.board.pv값이 있으면 그대로 없으면 1을 넣는다.
  const pv = useSelector((state) =>
    state.notice.pv ? state.notice.pv : { currentPage: 1 }
  );

  const getNoticeList = (currentPage) => {
    dispatch(noticeActions.getNoticeList(currentPage));
    navigator(`/admin/notice/list/${currentPage}`); // 강제적인 페이지 이동
  };

  useEffect(() => {
    getNoticeList(currentPage); // 1번을 넘겨줌
  }, []); // 한번만 요청이 되도록 설정

  return (
    <div>
      <Link className="btn btn-danger" to="/admin/notice/write">
        글쓰기
      </Link>
      <h3 className="text-center">게시판 목록</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <colgroup>
          <col width="8%" />
          <col width="*" />
          <col width="12%" />
          <col width="12%" />
        </colgroup>

        <thead>
          <tr>
            <th>번호</th>
            <th>제목</th>
            <th>작성자</th>
            <th>작성일자</th>
          </tr>
        </thead>

        <tbody>
          {noticeList &&
            noticeList.map((notice) => {
              return <TableRow notice={notice} key={notice.num} />;
            })}
        </tbody>
        {/* 처음에는 0출력, 화면에 출력되는 렌더링이 끝나면 useEffect가 수행되면서 백엔드에 요청을 하게 됨(board_action.js에서) */}
      </table>

      {pv ? <PageNavigation getNoticeList={getNoticeList} /> : ""}
    </div>
  );
};

export default NoticeList;
