import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { videoActions } from "../../reduxs/actions/video_action";
import { useEffect } from "react";

const VideoView = () => {
  const { num } = useParams();
  const dispatch = useDispatch();
  const navigator = useNavigate();
  const videoDetail = useSelector((state) => state.video.videoDetail);
  const pv = useSelector((state) => state.video.pv);

  useEffect(() => {
    dispatch(videoActions.getVideoDetail(num));
  }, [dispatch, num]);

  return (
    <div>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <tbody>
          <tr>
            <th width="20%">글쓴이</th>
            <td>
              <p>테스트</p>
            </td>
          </tr>

          <tr>
            <th>제목</th>
            <td colSpan="3">{videoDetail.videoTitle}</td>
          </tr>

          <tr>
            <th>사용자 고유번호</th>
            <td colSpan="3">{noticeDetail.adminNum}</td>
          </tr>

          <tr>
            <th>글 고유번호</th>
            <td colSpan="3">{noticeDetail.noticeNum}</td>
          </tr>

          <tr>
            <th>작성일자</th>
            <td colSpan="3">{noticeDetail.writeDate}</td>
          </tr>

          <tr>
            <th>내용</th>
            <td colSpan="3">{noticeDetail.noticeContent}</td>
          </tr>
        </tbody>
      </table>

      <Link
        className="btn btn-primary"
        to={`/admin/notice/list/${pv.currentPage}`}
      >
        리스트
      </Link>

      {/* {localStorage.getItem("memberEmail") ===
      (boardDetail["memberEmail"] ? boardDetail["memberEmail"] : null) ? (
        <> */}
      <Link className="btn btn-primary" to={`/admin/notice/update/${num}`}>
        수정
      </Link>

      <button className="btn btn-primary" onClick={handleDelete}>
        삭제
      </button>
      {/* </> */}
      {/* ) : null} */}
    </div>
  );
};

export default VideoView;
